html:
multiple card 

<style>
body{
     background: #bbb;
}
.container {
    background: url(/assets/eftc/images/CardBG.jpg);
    /* background: red; */
    background-repeat: no-repeat;
    background-size: 100% 100%;
    width: 1410px;
    height: 960px;
    transform: rotate(-90deg);
    position: absolute;
    margin-left: -14.3rem;
    margin-top: 11.5rem;
}

.print-preview-wrapper{
    padding:0px !important;
}
.container ul{
padding-top:1rem;
margin-left:4rem;
}
.print-format {
    padding:0 !important;
    /*background: #bbb;*/
}
.container ul li{
         font-weight: bold;
    list-style: none;
    text-transform: uppercase;
    padding-top: 0.2rem;
          font-size: 48px;
    line-height: 58px;
    color:black;
    white-space: nowrap;
  text-overflow: ellipsis;
  display: -webkit-box;
   -webkit-line-clamp: 2;
  max-width: 60rem;
    overflow-wrap: break-word;
    white-space: normal;
}
.image{
    width: 250px;
    height: 200px;
    padding-left: 1rem;
    margin-top: 20rem;
}
.flex{
    display: flex;
}
</style>

<body>
    {% for attendee in doc.attendees %}
   <div class="container">
    <div class="row" >
        <div class="col-4"  style="margin-left:6rem;">

            {% set certificate = frappe.get_doc('Certificate', {'training_schedule': doc.name, 'attendee_name': attendee.attendee_name}) %}
            {% set item = frappe.get_doc('Item', doc.course) %}
            {%  if  attendee.upload_photo == None %}
                {% set photo_url = "/assets/eftc/images/attendee.png" %}
            {% else %}
                {% set photo_url = attendee.upload_photo %}
            {% endif %}
          <img class = "image" src= "{{photo_url}}" >
          {% if certificate.other__certificate_number %}
       <p style="padding-left: 1rem;"><strong>{{ certificate.other__certificate_number }}</strong></p>
       {% else %}
       <p style="padding-left: 1rem;
        font-size: 35px;margin-top: 4rem;"><strong style="font-weight: bolder; color:black;">{{ certificate.name }}</strong></p>
       {% endif %}
        </div>
        <div class="col-4" style="margin-left: 17rem;
    margin-top: -21.6rem;">
        <ul>
            <li>NAME : {{attendee.attendee_name or ""}}</li>
            <li>IQAMA/ID NO : {{attendee.iqamaid_no or ""}}</li>
            <li>COURSE : {{item.item_name or ""}}</li>
            <li>ISSUED ON : {{attendee.issue_date or ""}}</li>
            <li>VALID UNTIL : {{attendee.expiry_date or ""}}</li>
        </ul>
        </div>
        <div class="col-4">
            <img style = "height: auto;
            width: 200px;
           margin-top: -4rem;
    margin-right: 5rem;
    float: right;"src={{doc.qr_image}} >
        </div>
     </div>
</div>
 <div style="page-break-before: always;"></div>
    {% endfor %}
