css:
table, th, td {
  border: 1px solid #f4f5f6;
}

.Divbox{
   border: 1px solid #f4f5f6;
height: auto;
width: 300px;
padding: 20px;
}

.barcode-box{
height: auto;
width: 300px
}

.barcode {
display: flex;
justify-content: space-between;
}

.bar-img{
    height:50px !important;
    width:200px;
}



.divone, .divtwo{
    background-color:#adabab;
}

*{
    font-style:oblique;
    font-family:cursive !important;
}
.full-format
{
   position: relative; 
}
.full-format .footer
{
    position: relative;
    bottom: 0;
}

.footer {
    text-align: center;
    position: fixed;
    bottom: 0;
    width: 100%;
    border-top: 1px solid #ccc;
    margin: 25px 0 0px 0;
}
p,tr {
    font-size: 12px !important ;
}
.print-format th {
     color: #fff; 
    font-weight: normal;
    border-bottom-width: 1px !important;
}

html:
{% set page = namespace(value=1) %}
 <!--Header Section-->
{% macro header() %}
    <div class="col-xs-12" >
        <img src="/assets/eftc/images/header.png" >
    </div>

{% endmacro %}


{% macro page_end() %}
    <div class="col-xs-12 footer">
            <img style = "margin-top:100px;" src="/assets/eftc/images/footer.png" >
        </div>
    <div style = "page-break-after: always;"></div>
  {% endmacro %}

{% macro address_section() %}
<div class="col-xs-12" style="margin-bottom: 25px;">
             <p class="heading-right"><b style="font-size: 20px;padding-left: 250px;">SALES QUOTATION</b></p>
     </div>

    <!--Body Section-->

    <div class = "row" style="margin-top:0px !important;">
   <div style="background-color:#cecece;border: 1px solid black !important;margin-top: 10.3rem;
    height: 13rem;width:95%;margin-left:20px;
">
        <div class = "col-xs-6" style = "margin-left: -2px;border-right:1px solid black;height:207px;">
            <p><b>To,</b><br> {{doc.contact_person}}</p>

            <p>Address :- {{doc.address_display}}</p>
        </div>
        <div class = "col-xs-6" style = "padding-left: 0px;font-size:12px;">
            <div class = "col-xs-5" style = "text-align:left;">Date:</div>
            <div class = "col-xs-7" style = "text-align:left;">{{doc.transaction_date}}</div>
             <div class = "col-xs-6" style = "text-align:left;width:40% !important">Quotation Ref:</div>
            <div class = "col-xs-6" style = "text-align:left;width:60% !important">{{doc.name}}</div>
            <div class = "col-xs-5" style = "text-align:left;">Sales Rep :</div>
            <div class = "col-xs-7" style = "text-align:left;">{{doc.custom_sales_representative}}</div> 
            <div class = "col-xs-5" style = "text-align:left;">Mobile No :</div>
            <div class = "col-xs-7" style = "text-align:left;">{{doc.sales_representative_mobile_number}}</div>
            <div class = "col-xs-5" style = "text-align:left;">Payment Terms :</div>
            <div class = "col-xs-7" style = "text-align:left;">100% Advance</div>
             <div class = "col-xs-5" style = "text-align:left;">Qtotation Validity:</div>
            <div class = "col-xs-7" style = "text-align:left;">{{doc.qr_code_validity_in_days}} Days</div>


            <div class = "col-xs-5" style = "text-align:left;">Training Mode :</div>
            <div class = "col-xs-7" style = "text-align:left;">{{doc.training_mode}}</div>
            <div class = "col-xs-5" style = "text-align:left;">Training Location :</div>
            <div class = "col-xs-7" style = "text-align:left;">{{doc.location}}</div>

   </div>
        </div>
    </div>

    <div class = "col-xs-12">
        <p style = "margin:10px 10px 35px 0px;">Dear Sir ,<br>We Are Greatly privileged to be given this chance to quote for your company . We have evaluated your requirements in details and we came up with a proposal that is appropriate for your need.</p>
    </div>

{% endmacro %}


{%  macro tablehead() %}
<div class ="row">
    <div class = "col-xs-12" style="margin-left: 20px;margin-right: 40px;">
                    <table class="table" style = "width:95%;">
                        <tr style="">
                            <th style = "width:10%;background: black !important;"><b style="color: white !important;">SL.NO</b></th>
                            <th style = "width:45%;background: black !important;"><b style="color: white !important;">COURSE</b></th>
                            <th style = "width:15%;background: black !important;"><b style="color: white !important;">AWARDING BODY</b></th>
                            <th style = "width:10%;background: black !important;"><b style="color: white !important;">UNIT PRICE</b></th>
                        <th style = "width:10%;background: black !important;"><b style="color: white !important;">QUANTITY</b></th>
                            <th style = "width:10%;background: black !important;"><b style="color: white !important;">TOTAL (SR)</b></th>
                        </tr>
{% endmacro %}


{% macro table() %}
{% set item_count = namespace(value=1) %}
{% set page_no = namespace(value = 1) %}
    {% for item in doc.items %}
    {% set item_count.value = item_count.value + 1 %}
      {% if (page_no.value == 1 and (item.idx == 7 or (6 >= doc.items|length and item.idx ==6 ) )) %}
        {% set page_no.value = page_no.value +1 %}
        {% set item_count.value = 1 %}
            </table>
            </div>
            </div>
            {% set page.value = page.value + 1 %}
           <p style = "margin-left: 299px;"> Page - {{page.value - 1}} </p>
            {{page_end()}}
            {{header()}}
            {{tablehead()}}

        {% elif (item_count.value == 15 and doc.items|length < item.idx +1) or item_count.value == 16  %}
        </table>
            </div>
            </div>
            {% set page.value = page.value + 1 %}
            <p style = "margin-left: 299px;"> Page - {{page.value - 1}} </p>
            {{page_end()}}
            {{header()}}
            {{tablehead()}}
        {% set item_count.value = 1 %}


        {% endif %}

        <tr>
              <td  style = "text-align:right;">{{item.idx}} </td>
              <td  >{{item.item_name}} </td>
              <td  >{{item.awarding_body}} </td>
              <td  >{{item.rate}} </td>
              <td  >{{item.qty}} </td>
              <td  >{{item.base_amount}} </td>
        </tr>

    {% endfor %}

            </table>
            </div>
            </div>

        {{sub_total()}}
        {{page_end()}}

{% endmacro %}

{% macro sub_total() %}
<div class ="row" style="">
    <div class = "col-xs-5">
        <img style = "height: 90px;width:90px;margin-left:44px"src= {{doc.qr_image}}>
    </div>
                <div class = "col-xs-7" style="width: 66%;
    padding: 0px;
    margin-top: -109px;
    float: right;    margin-right: 4px;
">
                    <table style = "width:95%;font-size: small;">
                        <tr> 
                            <td><b>SUB TOTAL </b></td>
                            <td >{{ doc.get_formatted("total") }}</td>
                        </tr>
                        <tr> 
                            <td><b> VAT TOTAL </b></td>
                            <td > {{ doc.get_formatted("total_taxes_and_charges") }}</td>
                        </tr>
                        <tr> 
                            <td><b>GRAND TOTAL </b></td>
                            <td > {{ doc.get_formatted("base_grand_total") }}</td>
                        </tr>
                    </table>
                </div>
    </div>

{% endmacro %}


{% macro terms() %}
    {% if doc.tc_name %}
    {{header()}}
   <div class = "row" style = "margin-left:20px;margin-top:30px;width:95%;">
       <div style = "padding: 5px;font-size:16px !important;"><p><b>Terms and Conditons:</b></div>
    <br>{{doc.terms}}</p>

    {% endif %}
{% endmacro %}





<!--Calling Header -->
{{header()}}

<!--Calling Address Section-->
{{address_section()}}

<!--Calling Table Head -->
{{tablehead()}}

{{table()}}

{{terms()}}
