 {% macro first_page(doc) %}


    <table class="table">
        <tr>
            <th style = "width:5%;color:black !important; background-color:#d7d7d7; text-align:center;"><b>#</b></th>
                <th style = "width:30%;color:black !important; background-color:#d7d7d7; text-align:center;">طبيعة السلع/الخدمة<br>Nature of Goods /Service</th>
                <th style = "width:12%;color:black !important; background-color:#d7d7d7; text-align:center;">سعر الوحدة <br> Unit Price</th>
                <th style = "width:10%;color:black !important; background-color:#d7d7d7; text-align:center;">كمية <br>Qty</th>
                <th style = "width:15%;color:black !important; background-color:#d7d7d7; text-align:center;">المبلغ الخاضع للضريبة <br> Taxable Amt</th>
                <th style = "width:5%;color:black !important; background-color:#d7d7d7; text-align:center;">ﺿﺮﻳﺒﺔ<br>VAT(%)</th> 
                <th style = "width:10%;color:black !important; background-color:#d7d7d7; text-align:center;">قيمة الضريبة<br> VAT AMT</th>
                <th style = "width:12%;color:black !important; background-color:#d7d7d7; text-align:center;">الفرعي للعنصر<br>Item Subtotal (Inc. VAT)</th>
        </tr>

        {% for item in doc.items %}
            {% if item.idx <= 2 %}
                <tr>
                    <td style="text-align:right;">{{ item.idx }}</td>
                        <td style="text-align:center;">{{ item.item_name }}</td>
                        <td style="text-align:center;">{{ item.rate }}</td>
                        <td style="text-align:center;">{{ item.qty }}</td>
                        <td style="text-align:center;">{{ item.base_amount }}</td>
                        <td style="text-align:center;">{{ doc.taxes[0].rate }} %</td>
                        <td style="text-align:center;">{{ (item.rate*doc.taxes[0].rate)/100 }}</td>
                        <td style="text-align:center;">{{ item.base_amount }}</td>
                </tr>
            {% endif %}
        {% endfor %}
    </table>
    {% if doc.items|length < 3 %}
    {{ page_end(1) }}
    <div class="page-break"></div>
    {{ header()}}
    {{sub_total()}}
    {{bank_details()}}
    {{ page_end(2) }}
    {% else %}
    {{ page_end(1) }}
    {% endif %}
{% endmacro %}

{% macro other_pages(doc) %}

